		AND a.user_login_name = #{userLoginName}


	    AND u2.user_type = #{userType} 


	    AND a.user_sex = #{userSex} 


	    AND a.user_age >= #{beginAge} 


	    AND a.user_age <= #{endAge} 


		AND UNIX_TIMESTAMP(a.start_time) >= UNIX_TIMESTAMP(#{startTime}) 


		AND UNIX_TIMESTAMP(a.end_time) < (UNIX_TIMESTAMP(#{endTime})+24*3600)  


		AND a.org_id = #{orgId}


		AND a.org_id IN(${organization})




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT count(distinct su.task_number) AS "checkTotalCount", count(distinct case su.state when 2 then su.task_number end) AS "checkFinishedCount", count(distinct case su.state when 0 then su.task_number end) AS "checkUnfinishedCount", count(distinct case sc.abnormal when 1 then sc.task_number end) AS "checkAbnormalCount", count(distinct su.task_number) - count(distinct case su.state when 2 then su.task_number end) AS "checkInvalidCount" FROM scale_task_user su left join scale_check_score sc ON su.task_number = sc.task_number WHERE su.del_flag = #{DEL_FLAG_NORMAL}
AND su.tid = #{tid}
AND UNIX_TIMESTAMP(su.start_time) >= UNIX_TIMESTAMP(#{startTime})
AND UNIX_TIMESTAMP(su.end_time) < (UNIX_TIMESTAMP(#{endTime})+24*3600)
AND su.org_id = #{orgId}

